import React from 'react';

class DevToLinks extends React.Component {
constructor(props){ 
    super(props);
    this.state = {
        dev_data : []
    }

    }

    GetDevToData(){ 
        let curState = this;
         fetch('https://dev.to/api/articles?username=andhop&page=1&per_page=3')
                .then(response => response.json())
                .then(jsdata => {
                    this.setState({
                        dev_data: jsdata
                    });
                }) 



    }
    render(){
        this.GetDevToData();
        const cards = [];
        this.state.dev_data.forEach(article => {
            cards.push(
                <div className="card">
                    <span className="dev_art_title"></span>
                </div>
            );
        });

        return (
            <div className="content-row">
                {cards}
            </div>
        )
    }
}

export default DevToLinks;
