import React from 'react';

class DevToLinks extends React.Component {
constructor(props){ 
    super(props);
    this.state = {
        dev_data : null
    }

    }

    GetDevToData(){ 
        return fetch('https://dev.to/api/articles?username=andhop&page=1&per_page=3')
                .then(function(response){
                    this.setState({
                        dev_data: response
                    }) 
                });
    }
    render(){
        this.GetDevToData();
        const cards = [];
        this.state.dev_data.forEach(article => {
            cards.push(
                <div className="card">
                    <span className="dev_art_title"></span>
                </div>
            );
        });

        return (
            <div className="content-row">
                {cards}
            </div>
        )
    }
}

export default DevToLinks;
